import { useEffect, useState } from "react";
import Button from "../ui/Button/Button";
import StyledHero from "./Hero.Styled.js";
import Image from "../ui/Button/Image/Image";
import Paragraph from "../ui/Button/Paragraph/Paragraph";
import axios from "axios";
import getEndpointURL from "../../utils/constants/endpoints";
// import { useParams } from "react-router-dom";

function Hero() {
  // const { id } = useParams();
  const [movie, setMovie] = useState("");
  const genres = movie && movie.genres.map((genre) => genre.name).join(", ");
  const trailer = movie && movie.videos.results[0].key;

  useEffect(() => {
    getDetailMovie();
  }, []);

  async function getTrendingMovies() {
    const endpointURL = getEndpointURL();
    const response = await axios(endpointURL.HERO);
    return response.data.results[0];
  }

  async function getDetailMovie() {
    const trendingMovie = await getTrendingMovies();
    const movieId = trendingMovie.id;

    const endpointURL = getEndpointURL(movieId);
    const response = await axios(endpointURL.DETAIL);

    setMovie(response.data);
  }

  return (
    <StyledHero>
      <div>
        <section>
          <div className="left">
            <h2>{movie.title}</h2>
            <h3>{genres}</h3>
            <Paragraph>{movie.overview}</Paragraph>
            <Button
              as="a"
              href={`https://www.youtube.com/watch?v=${trailer}`}
              target="_blank"
            >
              Watch Movie
            </Button>
          </div>
          <div className="right">
            <Image
              src={`http://image.tmdb.org/t/p/w300/${movie.poster_path}`}
              alt={movie.Title}
            />
          </div>
        </section>
      </div>
    </StyledHero>
  );
}

export default Hero;
