/* eslint-disable no-template-curly-in-string */
import { useEffect, useState } from "react";
import Button from "../ui/Button/Button";
import StyledHero from "./Hero.Styled.js";  
import Image from "../ui/Button/Image/Image";
import Paragraph from "../ui/Button/Paragraph/Paragraph";
import axios from "axios";

function Hero() {
  const [movie, setMovie] = useState("");
  // eslint-disable-next-line no-unused-vars
  const API_KEY = process.env.REACT_APP_API_KEY;
  const genres = movie && movie.genres.map((genre) => genre.name).join(",");
  const trailer = movie && `https://www.youtube.com/watch?v=${movie.videos.results[0].key}`;

  console.log(trailer);

  // eslint-disable-next-line react-hooks/exhaustive-deps
  useEffect(getDetailMovie, []);

  // eslint-disable-next-line react-hooks/exhaustive-deps
  async function getDetailMovies() {
    // eslint-disable-next-line no-template-curly-in-string
    const url = `https://api.themoviedb.org/3/trending/movie/day?api_key=${API_KEY}`;
    // Melakukan Fetch data dari API omdb.
    const response = await axios(url);
   return response.data.results[0];
  }

  // eslint-disable-next-line react-hooks/exhaustive-deps
  async function getDetailMovie() {
    const trendingMovie = await getDetailMovies();
    const id = trendingMovie.id;

    const URL = `https://api.themoviedb.org/3/movie/${id}?api_key=${API_KEY}&append_to_response=videos`;
    const response = await axios(URL);
    console.log(response.data);

    setMovie(response.data);

  }

  return (
    <StyledHero>
    <div>
      <section>
        <div class="left">
          <h2>{movie.Title}</h2>
          <h3>{genres}</h3>
          <Paragraph>{movie.overview}</Paragraph>
          <Button 
            as="a" 
            href={trailer}
            target="_blank"
          >Watch Movie</Button>

        </div>
        <div class="right">
          <Image 
          src={`https://image.tmdb.org/t/p/w500/${movie.backdrop_path}`} 
          alt="placeholder"/>
        </div>
      </section>
    </div>
    </StyledHero>
  );
}

export default Hero;
